let bcrypt,model,Schema;_cf0‍.w("bcrypt",[["default",["bcrypt"],function(v){bcrypt=v}]]);_cf0‍.w("mongoose",[["model",["model"],function(v){model=v}],["Schema",["Schema"],function(v){Schema=v}]]);


const userSchema = new Schema({
  firstName: String,
  lastName: String,
  email: {
    type: String,
    required: [true, "User email required"],
    index: { unique: true },
    validate: {
      validator: function (email) {
        return /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
          email
        );
      },
      message: (props) => `${props.value} is not a valid email ID!`,
    },
  },
  phone: {
    type: String,
    index: { unique: true },
    validate: {
      validator: function (phone) {
        return /^\s*(?:\+?(\d{1,3}))?[-. (]*(\d{3})[-. )]*(\d{3})[-. ]*(\d{4})(?: *x(\d+))?\s*$/.test(phone);
      },
      message: (props) => `${props.value} is not a valid phone number!`,
    },
    required: [true, "User phone number required"],
  },
  password: { type: String, required: true },
  createdAt: { type: Date, default: Date.now },
  isActive: { type: Boolean, default: true },
  isDeleted: { type: Boolean, default: false },
  isResetPasswordInitiated: { type: Boolean, default: false },
});

// hash password before saving
userSchema.pre("save", async function () {
  let user = this;

  // basic input validation
  let error = user.validateSync();
  if (error) throw new Error(error);

  // generate a 12 round salt
  const salt = await bcrypt.genSalt(12);
  // hash the password using the salt
  const hashedPassword = await bcrypt.hash(user.password, salt);

  // override the cleartext password with the hashed one and activate user
  user.password = hashedPassword;
});

userSchema.methods.comparePassword = async function (inputPassword) {
  return await bcrypt.compare(inputPassword, this.password);
};

userSchema.methods.findActiveUser = async function (input) {
  if (input.includes("@")) return await this.model("User").findOne({ email: input, isActive: true, isDeleted: false });
  return await this.model("User").findOne({ phone: input, isActive: true, isDeleted: false });
};

module.exports = model("User", userSchema);
